// Archivo principal de la aplicación
class App {
    constructor() {
        this.initialized = false;
        this.managers = {};
        this.init();
    }

    // Inicializar la aplicación
    async init() {
        try {
            console.log('🚀 Iniciando aplicación...');
            
            // Verificar dependencias
            this.checkDependencies();
            
            // Configurar event listeners globales
            this.setupGlobalEventListeners();
            
            // Configurar interceptores
            this.setupInterceptors();
            
            // Inicializar managers
            this.initializeManagers();
            
            // Configurar estado inicial
            await this.setupInitialState();
            
            this.initialized = true;
            console.log('✅ Aplicación inicializada correctamente');
            
        } catch (error) {
            console.error('❌ Error inicializando aplicación:', error);
            this.showInitializationError(error);
        }
    }

    // Verificar que todas las dependencias estén disponibles
    checkDependencies() {
        const requiredGlobals = [
            'CONFIG', 'buildUrl', 'getAuthHeaders', 'isAuthenticated',
            'authManager', 'apiManager', 'uiManager'
        ];

        const missing = requiredGlobals.filter(dep => !window[dep]);
        
        if (missing.length > 0) {
            throw new Error(`Dependencias faltantes: ${missing.join(', ')}`);
        }
    }

    // Configurar event listeners globales
    setupGlobalEventListeners() {
        // Manejar errores no capturados
        window.addEventListener('error', (e) => {
            console.error('Error no capturado:', e.error);
            this.handleGlobalError(e.error);
        });

        // Manejar promesas rechazadas
        window.addEventListener('unhandledrejection', (e) => {
            console.error('Promesa rechazada:', e.reason);
            this.handleGlobalError(e.reason);
        });

        // Manejar cambios de conectividad
        window.addEventListener('online', () => {
            showToast('Conexión restaurada', 'success');
        });

        window.addEventListener('offline', () => {
            showToast('Sin conexión a internet', 'warning');
        });

        // Manejar cambios de visibilidad de la página
        document.addEventListener('visibilitychange', () => {
            if (!document.hidden && this.initialized) {
                this.handlePageVisible();
            }
        });

        // Prevenir envío de formularios por defecto
        document.addEventListener('submit', (e) => {
            // Solo prevenir si no tiene un handler específico
            if (!e.target.hasAttribute('data-allow-default')) {
                e.preventDefault();
            }
        });

        // Manejar teclas globales
        document.addEventListener('keydown', (e) => {
            this.handleGlobalKeydown(e);
        });
    }

    // Configurar interceptores
    setupInterceptors() {
        // Ya configurado en auth.js, pero podemos agregar más lógica aquí
        console.log('📡 Interceptores configurados');
    }

    // Inicializar managers
    initializeManagers() {
        this.managers = {
            auth: window.authManager,
            api: window.apiManager,
            ui: window.uiManager,
            alumnos: window.alumnosManager,
            cursos: window.cursosManager,
            inscripciones: window.inscripcionesManager
        };

        console.log('🔧 Managers inicializados:', Object.keys(this.managers));
    }

    // Configurar estado inicial
    async setupInitialState() {
        // Verificar autenticación
        if (this.managers.auth.isLoggedIn()) {
            console.log('👤 Usuario autenticado, cargando datos iniciales...');
            await this.loadInitialData();
        } else {
            console.log('🔐 Usuario no autenticado, mostrando login...');
            this.managers.auth.showLogin();
        }

        // Configurar tab inicial
        this.managers.ui.showTab('alumnos');
    }

    // Cargar datos iniciales
    async loadInitialData() {
        try {
            showLoading(true);
            
            // Cargar datos en paralelo
            await Promise.all([
                this.managers.alumnos.loadAlumnos(),
                this.managers.cursos.loadCursos()
            ]);
            
            console.log('📊 Datos iniciales cargados');
            
        } catch (error) {
            console.error('Error cargando datos iniciales:', error);
            showToast('Error al cargar datos iniciales', 'warning');
        } finally {
            showLoading(false);
        }
    }

    // ==================== MANEJO DE ERRORES ====================

    // Manejar errores globales
    handleGlobalError(error) {
        console.error('Error global:', error);
        
        // No mostrar toast para errores menores
        if (this.isMinorError(error)) {
            return;
        }

        let message = 'Ha ocurrido un error inesperado';
        
        if (error.message) {
            if (error.message.includes('fetch')) {
                message = 'Error de conexión. Verifique su internet.';
            } else if (error.message.includes('401')) {
                message = 'Su sesión ha expirado. Por favor, inicie sesión nuevamente.';
                this.managers.auth.handleTokenExpiry();
                return;
            } else {
                message = error.message;
            }
        }

        showToast(message, 'error');
    }

    // Verificar si es un error menor
    isMinorError(error) {
        if (!error || !error.message) return false;
        
        const minorErrors = [
            'ResizeObserver loop limit exceeded',
            'Non-Error promise rejection captured',
            'Script error'
        ];
        
        return minorErrors.some(minor => error.message.includes(minor));
    }

    // Mostrar error de inicialización
    showInitializationError(error) {
        const errorHtml = `
            <div style="
                position: fixed;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                background: #f8f9fa;
                display: flex;
                align-items: center;
                justify-content: center;
                z-index: 10000;
                font-family: Arial, sans-serif;
            ">
                <div style="
                    background: white;
                    padding: 40px;
                    border-radius: 8px;
                    box-shadow: 0 4px 20px rgba(0,0,0,0.1);
                    text-align: center;
                    max-width: 500px;
                ">
                    <h2 style="color: #dc3545; margin-bottom: 20px;">
                        <i class="fas fa-exclamation-triangle"></i>
                        Error de Inicialización
                    </h2>
                    <p style="margin-bottom: 20px; color: #666;">
                        No se pudo inicializar la aplicación correctamente.
                    </p>
                    <p style="margin-bottom: 30px; font-family: monospace; background: #f8f9fa; padding: 10px; border-radius: 4px;">
                        ${error.message}
                    </p>
                    <button onclick="location.reload()" style="
                        background: #007bff;
                        color: white;
                        border: none;
                        padding: 12px 24px;
                        border-radius: 4px;
                        cursor: pointer;
                        font-size: 16px;
                    ">
                        Recargar Página
                    </button>
                </div>
            </div>
        `;
        
        document.body.innerHTML = errorHtml;
    }

    // ==================== MANEJO DE EVENTOS ====================

    // Manejar cuando la página se vuelve visible
    async handlePageVisible() {
        // Verificar si el token sigue siendo válido
        if (!isAuthenticated()) {
            this.managers.auth.handleTokenExpiry();
            return;
        }

        // Refrescar datos si han pasado más de 5 minutos
        const lastRefresh = localStorage.getItem('last_data_refresh');
        const now = Date.now();
        
        if (!lastRefresh || (now - parseInt(lastRefresh)) > 5 * 60 * 1000) {
            console.log('🔄 Refrescando datos...');
            await this.loadInitialData();
            localStorage.setItem('last_data_refresh', now.toString());
        }
    }

    // Manejar teclas globales
    handleGlobalKeydown(e) {
        // Ctrl/Cmd + R: Recargar datos
        if ((e.ctrlKey || e.metaKey) && e.key === 'r') {
            e.preventDefault();
            this.refreshData();
        }

        // Escape: Cerrar modales
        if (e.key === 'Escape') {
            this.managers.ui.hideAllModals();
        }

        // F5: Recargar datos (alternativa)
        if (e.key === 'F5') {
            e.preventDefault();
            this.refreshData();
        }
    }

    // ==================== MÉTODOS PÚBLICOS ====================

    // Refrescar datos
    async refreshData() {
        try {
            showLoading(true);
            await this.loadInitialData();
            showToast('Datos actualizados', 'success');
        } catch (error) {
            console.error('Error refrescando datos:', error);
            showToast('Error al actualizar datos', 'error');
        } finally {
            showLoading(false);
        }
    }

    // Obtener estado de la aplicación
    getAppState() {
        return {
            initialized: this.initialized,
            authenticated: this.managers.auth?.isLoggedIn() || false,
            currentUser: this.managers.auth?.getCurrentUser() || null,
            activeTab: this.managers.ui?.getActiveTab() || null,
            managers: Object.keys(this.managers)
        };
    }

    // Obtener estadísticas generales
    async getGeneralStats() {
        try {
            const alumnos = this.managers.alumnos?.getAllAlumnos() || [];
            const cursos = this.managers.cursos?.getAllCursos() || [];
            
            return {
                totalAlumnos: alumnos.length,
                alumnosActivos: alumnos.filter(a => a.activo).length,
                totalCursos: cursos.length,
                cursosActivos: cursos.filter(c => c.activo).length,
                lastUpdate: new Date().toISOString()
            };
        } catch (error) {
            console.error('Error obteniendo estadísticas:', error);
            return null;
        }
    }

    // Exportar datos (para debugging)
    exportData() {
        const data = {
            appState: this.getAppState(),
            alumnos: this.managers.alumnos?.getAllAlumnos() || [],
            cursos: this.managers.cursos?.getAllCursos() || [],
            config: CONFIG,
            timestamp: new Date().toISOString()
        };
        
        console.log('📤 Datos exportados:', data);
        return data;
    }

    // Limpiar datos de la aplicación
    clearAppData() {
        if (confirm('¿Está seguro de que desea limpiar todos los datos de la aplicación?')) {
            localStorage.clear();
            sessionStorage.clear();
            location.reload();
        }
    }

    // ==================== MÉTODOS DE DESARROLLO ====================

    // Modo debug
    enableDebugMode() {
        window.DEBUG_MODE = true;
        window.app = this;
        console.log('🐛 Modo debug activado');
        console.log('Usa window.app para acceder a la instancia de la aplicación');
    }

    // Información de la aplicación
    getAppInfo() {
        return {
            name: 'Sistema de Gestión - Alumnos y Cursos',
            version: '1.0.0',
            author: 'Manus AI',
            description: 'Frontend para gestión de alumnos y cursos con relación muchos a muchos',
            technologies: ['HTML5', 'CSS3', 'JavaScript ES6+', 'JWT', 'REST API'],
            features: [
                'Autenticación JWT',
                'CRUD de Alumnos',
                'CRUD de Cursos', 
                'Gestión de Inscripciones',
                'Relación Muchos a Muchos',
                'Búsqueda y Filtros',
                'Interfaz Responsiva'
            ]
        };
    }
}

// Inicializar aplicación cuando el DOM esté listo
document.addEventListener('DOMContentLoaded', () => {
    window.app = new App();
    
    // Activar modo debug en desarrollo
    if (location.hostname === 'localhost' || location.hostname === '127.0.0.1') {
        window.app.enableDebugMode();
    }
});

// Funciones globales de utilidad
window.refreshData = function() {
    if (window.app) {
        window.app.refreshData();
    }
};

window.getAppInfo = function() {
    if (window.app) {
        console.table(window.app.getAppInfo());
    }
};

window.exportAppData = function() {
    if (window.app) {
        return window.app.exportData();
    }
};

window.clearAppData = function() {
    if (window.app) {
        window.app.clearAppData();
    }
};

